<?phprequire("../model/class.qualificacoesDAO.php");/** * Classe que representa uma extensão da classe DAO e inclui métodos * específicos para a tabela qualificacoes, base T-shaped. */class qualificacoesDAOExt extends qualificacoesDAO {    function buscaQualificacao($nome){                if(!empty($nome)){            //Pesquisa nome            list($countReg, $Qualif) = qualificacoesDAOExt::select(" NOME = '".$nome."'");                      if($countReg > 0){ //SE existe esse nome            //print_r($Qualif);                //retorna o id                $idQualificacao  = $Qualif[0]->getId();                                 return $idQualificacao;                            }            else{ //Não existe //insere e retorna o id                                $DadosQuali   = new qualificacoesDAOExt();                //Seta valores no objeto para inserir na tb qualificacoes                $DadosQuali->setNome($nome);                //Insere registros na tabela qualificacoes                list($codErro, $msgErro, $DadosQuali) = QualificacoesDAOExt::insert($DadosQuali);                  if( isset($codErro) && ($codErro == '0') ){                                        $idQualificacao  = $DadosQuali->getId();                                        return $idQualificacao;                }                                                }        }            }}?>