<?phprequire("../model/class.qualificacoes_usuariosDAO.php");/** * Classe que representa uma extensão da classe DAO e inclui métodos * específicos para a tabela qualificacoes_usuarios, base T-shaped. */class qualificacoes_usuariosDAOExt extends qualificacoes_usuariosDAO {    public function getDePara() {        list($countReg, $DadosDePara) = de_paraDAOExt::select(" de = ".$this->nota);        if ($countReg > 0)             return $DadosDePara[0];        else            return null;    }        public function getQualificacao() {        list($countReg, $Dados) = qualificacoesDAOExt::select(" id = ".$this->qualificacoes_id);        if ($countReg > 0)             return $Dados[0];        else            return null;    }    }?>